import React from 'react';
import styles from './ButtonGrid.module.css';

function ButtonGrid() {
  const buttons = [
    { text: 'Customer Manager', type: 'normal' },
    { text: 'Task Manager', type: 'normal' },
    { text: 'Inspection Manager', type: 'normal' },
    { text: 'Task Map', type: 'normal' },
    { text: 'Quick Lookup', type: 'normal' },
    { text: 'My Task', type: 'normal' },
    { text: 'My Inspection', type: 'normal' },
    { text: 'Inspection Map', type: 'normal' },
    { text: 'Scan QR Code', type: 'normal' },
    { text: 'My Task History', type: 'normal' },
    { text: 'My Inspection History', type: 'normal' },
    { text: 'Asset Map', type: 'normal' },
  ];

  return (
    <div className={styles.buttonGrid}>
      {buttons.map((button, index) => (
        <button 
          key={index} 
          className={`grid-button ${button.type}`}
        >
          {button.text}
        </button>
      ))}
    </div>
  );
}

export default ButtonGrid;
